import { useLoaderData, json, defer, Await } from "react-router-dom";

import EventsList from "../components/EventsList";

function EventsPage() {
  const { events } = useLoaderData();

  return (
    <Await resolve={events}>
      {(loadedEvents) => <EventsList events={loadEvents} />}
    </Await>
  );
}

export default EventsPage;

async function loadEvents() {
  const response = await fetch("http://localhost:8080/events");

  if (!response.ok) {
    // return { isError: true, message: "couldn't fetch events!" };
    // throw new Response(JSON.stringify({ message: "couldn't fetch events!" }), {
    //   status: 500,
    return json({ message: "couldn't fetch events!" }, { status: 500 });
  } else {
    return response;
  }
}
export function loader(params) {
  return defer({
    events: loadEvents(),
  });
}
